contracts/PaymentProcessor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract PaymentProcessor is ReentrancyGuard, Ownable {
    IERC20 public usdt;
    
    // Exchange rate: 1 USDT = 100 Coins (scaled by 1e18)
    uint256 public constant EXCHANGE_RATE = 100 * 1e18;
    
    // User balances (coins)
    mapping(address => uint256) public userCoins;
    
    // Events
    event Deposit(address indexed user, uint256 usdtAmount, uint256 coinsAmount);
    event Withdraw(address indexed user, uint256 coinsAmount, uint256 usdtAmount);
    event CoinsTransferred(address indexed from, address indexed to, uint256 amount);
    
    constructor(address _usdtAddress) {
        usdt = IERC20(_usdtAddress);
    }
    
    /**
     * @dev Deposit USDT and receive coins
     * @param amount Amount of USDT to deposit (in wei)
     */
    function depositUSDT(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be greater than 0");
        require(usdt.balanceOf(msg.sender) >= amount, "Insufficient USDT balance");
        require(usdt.allowance(msg.sender, address(this)) >= amount, "Insufficient USDT allowance");
        
        // Calculate coins to give (1 USDT = 100 coins)
        uint256 coinsAmount = (amount * EXCHANGE_RATE) / 1e18;
        
        // Transfer USDT from user to contract
        require(usdt.transferFrom(msg.sender, address(this), amount), "USDT transfer failed");
        
        // Add coins to user balance
        userCoins[msg.sender] += coinsAmount;
        
        emit Deposit(msg.sender, amount, coinsAmount);
    }
    
    /**
     * @dev Withdraw USDT by burning coins
     * @param coinsAmount Amount of coins to burn
     */
    function withdrawUSDT(uint256 coinsAmount) external nonReentrant {
        require(coinsAmount > 0, "Amount must be greater than 0");
        require(userCoins[msg.sender] >= coinsAmount, "Insufficient coins balance");
        
        // Calculate USDT to return
        uint256 usdtAmount = (coinsAmount * 1e18) / EXCHANGE_RATE;
        require(usdt.balanceOf(address(this)) >= usdtAmount, "Insufficient USDT in contract");
        
        // Burn coins
        userCoins[msg.sender] -= coinsAmount;
        
        // Transfer USDT to user
        require(usdt.transfer(msg.sender, usdtAmount), "USDT transfer failed");
        
        emit Withdraw(msg.sender, coinsAmount, usdtAmount);
    }
    
    /**
     * @dev Get user's coin balance
     * @param user User address
     * @return User's coin balance
     */
    function getUserCoins(address user) external view returns (uint256) {
        return userCoins[user];
    }
    
    /**
     * @dev Get current exchange rate
     * @return Exchange rate (coins per USDT)
     */
    function getExchangeRate() external pure returns (uint256) {
        return EXCHANGE_RATE;
    }
    
    /**
     * @dev Transfer coins between users (for donations)
     * @param to Recipient address
     * @param amount Amount of coins to transfer
     */
    function transferCoins(address to, uint256 amount) external {
        require(to != address(0), "Cannot transfer to zero address");
        require(amount > 0, "Amount must be greater than 0");
        require(userCoins[msg.sender] >= amount, "Insufficient coins balance");
        
        userCoins[msg.sender] -= amount;
        userCoins[to] += amount;
        
        emit CoinsTransferred(msg.sender, to, amount);
    }
    
    /**
     * @dev Emergency function to withdraw USDT (owner only)
     * @param amount Amount of USDT to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        require(usdt.balanceOf(address(this)) >= amount, "Insufficient USDT balance");
        require(usdt.transfer(owner(), amount), "USDT transfer failed");
    }
    
    /**
     * @dev Get contract's USDT balance
     * @return Contract's USDT balance
     */
    function getContractUSDTBalance() external view returns (uint256) {
        return usdt.balanceOf(address(this));
    }
}

